export const ADD_FAV = "ADD_FAV"
export const DELETE_FAV = "DELETE_FAV"

//! Pone al caracter en Fav
export const add_fav = (character) =>{
    return{
        type:ADD_FAV,
        payload: character,
    }
}

//! Quita al caracter de dicho Id de Fav
export const delete_fav = (id) =>{
    return{
        type:DELETE_FAV,
        payload: id,
    }
}
