import { ADD_FAV,DELETE_FAV } from "./actions";

//! Inicializar el Estado Global
const initialState = {
    myFavorites: [],
};

//! Reducer: Devuelve un nuevo (si no hay algun error)Estado actualizado de la APP
const rootReducer = (state = initialState, action) => {
    switch (action.type) {    //! Evalua el typo de la accion
        case ADD_FAV:
            return {
                ...state,     //! Si es ADD_FAV el type, lo agrega a la array myFavorites del Estado Global 
                myFavorites: [...state.myFavorites, action.payload]
            };
        case DELETE_FAV:
            return{
                ...state,     //! Si es DELETE_FAV el type, filtra, quitando solo el character con dicha id de myFavorites
                myFavorites: state.myFavorites.filter(
                    (char) => char.id !== action.payload
                ),
            };
            default:
        return {...state};
    } 
};

export default rootReducer;
