import { ADD_FAV, DELETE_FAV,ORDER,FILTER } from "./action-types";

//! Inicializar el Estado Global
const initialState = {
  myFavorites: [],
  allCharacters: [],
};

//! Reducer: Devuelve un nuevo (si no hay algun error)Estado actualizado de la APP
const rootReducer = (state = initialState, action) => {
  switch (
    action.type 
  ) {
    case ADD_FAV:
      return {
        ...state, 
        myFavorites: [...state.allCharacters, action.payload],
        allCharacters: [...state.allCharacters,action.payload]
      };
    case DELETE_FAV:
      return {
        ...state, 
        myFavorites: myFavorites.filter(
          (char) => char.id !== action.payload),

      };
     case FILTER:
        return{
         ...state,
         myFavorites: [allCharacters.filter(char=>char.gender === action.payload)],
        };
    case ORDER:
        const ordenarCarac = [...allCharacters];
        if(action.payload === "Ascendente"){
            ordenarCarac.sort()
        } else{
            ordenarCarac.sort().reverse();
        }
        return{
            ...state,
            myFavorites: ordenarCarac,
        }
        

    default:
      return {...state,};
  }
};

export default rootReducer;
