class Card extends React.Component{
   constructor(props){
      super(props)
   }
   
   render(){
      return(
         <div className={style.DivCard}>
                  <div className={style.ButtonDiv}>
                     <button className={style.Button} onClick={()=>this.props.onClose(this.props.id)}>X</button>
                  </div>
                  <Link to={`/detail/${this.props.id}`}>
                     <h2>{this.props.name}</h2>
                   </Link>
                  
                  <img className={style.img} src={this.props.image} alt="" />
                   <div className={style.TextDiv}>
                     <h2>{this.props.species}</h2>
                     <h2>{this.props.gender}</h2>
                  </div>
               </div>
      )
   }

   
   
};

const handleFavorite = () => {
   if(isFav===true){
      setIsFav({...isFav,isFav: false})
      dispatch(delete_fav(this.character.id))
   }
}

const [isFav,setIsFav] = useState(false);

const mapDispatchToProps = (dispatch) => {
   return{
      add_fav: ()=>{
         dispatch(add_fav());
      },
      delete_fav: ()=>{
         dispatch(delete_fav());
      },
   }
}

export default connect(mapDispatchToProps,null)(Card)
