import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import style from "./Detail.module.css";
import { useDispatch, useSelector } from "react-redux";
import { getCharacterDetail, cleanDetail } from "../../redux/actions";

const Detail = () => {
  //hooks
  const { detailId } = useParams();
  const character = useSelector((state) => state.getCharacterDetail);
  const dispatch = useDispatch();

  //componentDidMount           //Monta el componente
  //componentDidUpdate          // Se actualiza cada vez que su dependencia cambia
  //componentWillUnmount       // Se desmonta

  useEffect(() => {
    dispatch(getCharacterDetail(detailId));

    return () => {
      dispatch(cleanDetail());
    };
  }, [detailId]);

  return (
    <div className={style.containerDetail}>
      {character.name ? (
        <>
          <div className={style.textDetail}>
            <h2>{character.name}</h2>
            <p>{character.status}</p>
            <p>{character.specie}</p>
            <p>{character.gender}</p>
            <p>{character.origin?.name}</p>
          </div>
          <div>
            <img src={character.image} className={style.imgDetail} alt="img" />
          </div>
        </>
      ) : (
        <h3>Loading...</h3>
      )}
    </div>
  );
};

export default Detail;
