const initialState = {
    myFavorites: [],
};

const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case "addFAV":
            return {
                ...state,
                myFavorites: [...state.myFavorites, action.payload]
            };
        case "deleteFav":
            return{
                ...state,
                myFavorites: state.myFavorites.filter(
                    (char) => char.id !== action.payload
                ),
            };
            default:
        return {...state};
    } 
};




export default rootReducer;
