export default function validation(userData) {
    //username
    let errors = {};

    if(/^\w+([-]?\w+)*@\w+([-]?\w+)*(\.\w{2,3})+$/.test(userData.username)){
        errors.username = "El email es invalido"
    }
    if(!userData.username){
        errors.username = "Este campo no puede estar vacio"
    }
    if(userData.username.length > 35){
        errors.username = "El mail no puede superara los 35 caracteres"
    }
    if(!userData.password.match(/\d/)){
        errors.password = "La contrase単a debe contener al menos un numero"
    }
    if(userData.password.length < 6 && userData.password.length > 10){
        errors.password = "La contrase単a debe contener entre 6 y 10 caracteres"
    }

    return errors;
}
