import { Link } from 'react-router-dom';
import style from './Card.module.css'
import { connect } from "react-redux"
import { add_fav, delete_fav } from '../../redux/actions';
import React, { useEffect, useState } from 'react';
import { useDispatch,useSelector } from 'react-redux';



const Card = (props) => {
   const [isFav, setIsFav] = useState(false);         //estado local Fav
   const dispatch = useDispatch();
   const state = useSelector();

   const handleFavorite = () => {     //Funcion HandleFavorite
      if (isFav === true) {                  //Setea en false el estado, despacha la funcion Delete_fav pansandole el id del personaje
         setIsFav(false)
         dispatch(delete_fav(props.id));
      }
      if (isFav === false) {
         setIsFav(true)                    //Setea en false el estado, despacha la funcion add_fav pansandole el id del personaje
         dispatch(add_fav(props.id));
      }

   }
   useEffect(() => {
      props.myFavorites.forEach((fav) => {
         if (fav.id === props.id) { 
            setIsFav(true);
         }
      });
   }, [props.myFavorites]);

   return (

      <div className={style.DivCard}>
         <div className={style.ButtonDiv}>
               {isFav ? (
                      <button onClick={handleFavorite}>‚ù§Ô∏è</button>
                   ) : (
                     <button onClick={handleFavorite}>ü§ç</button>
                   )}
            <button className={style.Button} onClick={() => props.onClose(props.id)}>X</button>
         </div>
         <Link to={`/detail/${props.id}`}>
            <h2>{props.name}</h2>
         </Link>

         <img className={style.img} src={props.image} alt="" />
         <div className={style.TextDiv}>
            <h2>{props.species}</h2>
            <h2>{props.gender}</h2>
         </div>
      </div>


   );

};



const mapStateToProps = (state) => {
   return {
      myFavorites: state.myFavorites
   }
};

export default connect(mapStateToProps,mapDispatchToProps)(Card)

