import { useDispatch, useSelector } from "react-redux";
import style from "./Favorites.module.css"
import {Link} from "react-router-dom"
import { filterCards, orderCards } from "../../redux/actions";

const Favorites = () => {
  const { myFavorites } = useSelector(state => state);
  const dispatch = useDispatch();

  const handleOrderCards= (event)=>{
    dispatch(orderCards(event.target.value))
  }
  const handleFilterCards = (event) =>{
    dispatch(filterCards(event.target.value))
  }

  return (
    <div>
      <div>
        <select name="orderCards" onChange={handleOrderCards}>
          <option value="Ascendente">Ascendente</option>
          <option value="Descendente">Descendente</option>
        </select>
        <select name="filterCards" onChange={handleFilterCards}>
          <option value="Male">Male</option>
          <option value="Female">Female</option>
          <option value="Genderless">Genderless</option>
          <option value="unknown">unknown</option>
        </select>
      </div>




      {myFavorites.map((character) => {
        return (
          <div className={style.DivCard}>
            <Link to={`/detail/${character.id}`}>
              <h2>{character.name}</h2>
            </Link>
            <img className={style.img} src={character.image} alt="" />
            <div className={style.TextDiv}>
              <h2>{character.species}</h2>
              <h2>{character.gender}</h2>
            </div>
          </div>
        );
      })}
      
    </div>
  );
};

export default Favorites;
