export default function validate(values){
    const errors = {};
    const regexEmail = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i;

    if(!regexEmail.test(values.username)){
        errors.name = "El nombre de usuario debe ser un email"
    }
    if(values.username.length === 0){
        errors.name = "El nombre de usuario no puede estar vacío"
    }
    if(values.username.length >35){
        errors.name = "El nombre de usuario no puede tener mas de 35 caracteres"
    }

    if(values.password.length === 0){
        errors.password = "La password tiene que tener al menos un número"
    }

    if(!(values.password.length >= 6 && values.password.length <=10)){
        errors.password = "La contraseña debe tener una longitud entre 6 y 10 caracteres"
    }

    return errors;
}
